func RandomToken() (string, error) {
	// Créer un slice de bytes pour stocker les octets aléatoires
	tokenBytes := make([]byte, 9)
	// Lire des octets aléatoires depuis la source cryptographique sécurisée
	_, err := rand.Read(tokenBytes)
	if err != nil {
		return "", err
	}

	// Encoder les octets en base64 pour obtenir une chaîne lisible
	token := base64.URLEncoding.EncodeToString(tokenBytes)

	return token, nil
}
